import { useState, useEffect } from "react";
import { Route, useParams, Routes } from "react-router-dom";
import { request } from "@octokit/request";

const RepoPage = () => {
    const { username, repoName } = useParams();
    const [resultGot, setResultGot] = useState();
    const [repoInfo, setRepoInfo] = useState({
        id: '',
        owner: '',
        name: '',
        starCnt: '',
        time: '',
        fullname: '',
        url: '',
        description: '',
    });
    const [errorFlag, setErrorFlag] = useState();

    useEffect(async() => {
            // console.log(username);
            try {
                // const result = await axios(listUrl);
                const result =  await request('GET /repos/{username}/{repo}', {
                    username: username,
                    repo: repoName
                })
                console.log("useruser: ", username);
                console.log(result);
                setResultGot(result);
                setErrorFlag(true);

            } catch(err) {
                if (err.name === "HttpError") {
                    // let checkUser = [false];
                    setErrorFlag(false);

                }

                // console.log(err.name);
            }

    }, [])

    useEffect(() => {
        if (errorFlag == true) {
            console.log("in");
            setRepoInfo({
                id: resultGot.data.id,
                owner: username,
                name: repoName,
                starCnt: resultGot.data.stargazers_count,
                time: resultGot.data.created_at,
                fullname: resultGot.data.full_name,
                url: resultGot.data.html_url,
                description: resultGot.data.description,
            });
            console.log("repoInfo", repoInfo);
        }
    }, [errorFlag])

    return ( 
        <div>
            <header className="user">
                { repoInfo.name }
            </header>
            <h2>
                {repoInfo.owner}
            </h2>
            <a href={repoInfo.url}>GitHub Page</a>
            <ul>
                <li>
                    fullname: {repoInfo.fullname}
                </li>
                <li>
                    stars: {repoInfo.starCnt}
                </li>
                <li>
                    created time: {repoInfo.time}
                </li>
                <li>
                    description: {repoInfo.description}
                </li>
            </ul>
        </div>
    );
}

export default RepoPage;
