     const [inputFlag, setInputFlag] = useState(true);
     const [searchFlag, setSearchFlag] = useState(false);
     const navigate = useNavigate();

     const handleChange = (e) => {
         setUsername(e.target.value);
         setPageNumber(1);
     }

     const handleClick = async () => {
         // console.log(username);
         try {
             // const result = await axios(listUrl);
             const result =  await request('GET /users/{username}/repos', {
                 username: username,
                 per_page: 10,
                 sort: 'created'
             })
             console.log("useruser: ", username);
             console.log(result);
             setRepo(result);
             setHttpFlag(true);
             setInputFlag(true);
         } catch(err) {
             if (err.name === "HttpError") {
                 // let checkUser = [false];
                 setHttpFlag(false);
                 setInputFlag(true);
             }
             if (username == '') {
                 setHttpFlag(false);
                 setInputFlag(false);
             }
             console.log("username", username);
         }
         console.log(repo);
         setSearchFlag(true);
         navigate(`/users/${username}/repos`);
     }

     console.log(httpFlag);

     console.log("continue");
     return ( 
